import requests;

class PyHTTP:
    def __init__(self):
        self.isBusy = False;

    def Get(self, url, payload = { }):
        self.isBusy = True;
        r = requests.get(url, payload);
        self.isBusy = False;
        return r;

    def Post(self, url, parameters):
        self.isBusy = True;
        r = requests.post(url,  data = parameters);
        self.isBusy = False;
        return r;